dt-example.js:
$('#example').DataTable({
	ajax: '../data/2500.txt',
	deferRender: true,
	initComplete: function () {
		this.api().row(1000).scrollTo();
	},
	scrollCollapse: true,
	scroller: true,
	scrollY: 200
});

dt-example.js-vanilla:
new DataTable('#example', {
	ajax: '../data/2500.txt',
	deferRender: true,
	initComplete: function () {
		this.api().row(1000).scrollTo();
	},
	scrollCollapse: true,
	scroller: true,
	scrollY: 200
});
