dt-example.js:
$('#example').DataTable({
	ajax: '../data/2500.txt',
	deferRender: true,
	scrollCollapse: true,
	scroller: true,
	scrollY: 200,
	select: true
});

dt-example.js-vanilla:
new DataTable('#example', {
	ajax: '../data/2500.txt',
	deferRender: true,
	scrollCollapse: true,
	scroller: true,
	scrollY: 200,
	select: true
});
